contracts/bqBTC.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.28;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract bqBTC is ERC20, Ownable {
    using SafeERC20 for IERC20;

    uint8 private _customDecimals;
    address public poolAddress;
    address public coverAddress;
    address public initialOwner;
    mapping(uint256 chainId => uint256 multiplier) public networkMultipliers;
    IERC20 public alternativeToken; // 0x6ce8da28e2f864420840cf74474eff5fd80e65b8
    address vaultContract;

    event Mint(
        address indexed account,
        uint256 amount,
        uint256 chainId,
        bool native
    );

    constructor(
        string memory name,
        string memory symbol,
        uint8 decimals_,
        uint256 initialSupply,
        address _initialOwner,
        address _alternativeToken,
        uint256 _multiplier
    ) ERC20(name, symbol) Ownable(_initialOwner) {
        require(
            _multiplier > 0 && _multiplier < 1100,
            "Multiplier must be greater than zero"
        );
        require(_alternativeToken != address(0), "Invalid token address");
        _customDecimals = decimals_;
        _mint(msg.sender, initialSupply);
        initialOwner = _initialOwner;
        alternativeToken = IERC20(_alternativeToken);
        networkMultipliers[block.chainid] = _multiplier;
    }

    function decimals() public view virtual override returns (uint8) {
        return _customDecimals;
    }

    function mint(address account, uint256 btcAmount) external payable {
        uint256 currentChainId = block.chainid;
        uint256 networkMultiplier = networkMultipliers[currentChainId];

        require(networkMultiplier > 0, "No multiplier for this network");
        require(account != address(0), "Invalid account address");

        bool nativeSent = msg.value > 0;
        bool btcSent = false;
        uint256 mintAmount;

        if (nativeSent) {
            mintAmount = (msg.value * networkMultiplier) / 1 ether;
        }

        if (!nativeSent) {
            mintAmount = (btcAmount * networkMultiplier) / 1 ether;
            require(btcAmount > 0, "amount must be greater than 0");
            alternativeToken.safeTransferFrom(
                msg.sender,
                address(this),
                btcAmount
            );
            btcSent = true;
        }

        require(nativeSent || btcSent, "Insufficient tokens sent to mint");
        _mint(account, mintAmount);

        emit Mint(account, mintAmount, currentChainId, nativeSent);
    }

    function burn(address account, uint256 amount) external {
        require(
            msg.sender == initialOwner ||
                msg.sender == poolAddress ||
                msg.sender == coverAddress,
            "not authorized to call he function"
        );
        _burn(account, amount);
    }

    function balanceOf(
        address account
    ) public view virtual override returns (uint256) {
        return super.balanceOf(account);
    }

    function bqMint(address account, uint256 amount) external onlyBQContracts {
        _mint(account, amount);
    }

    function transfer(
        address to,
        uint256 amount
    ) public virtual override returns (bool) {
        return super.transfer(to, amount);
    }

    function transferFrom(
        address from,
        address to,
        uint256 amount
    ) public virtual override returns (bool) {
        return super.transferFrom(from, to, amount);
    }

    function approve(
        address spender,
        uint256 amount
    ) public virtual override returns (bool) {
        return super.approve(spender, amount);
    }

    function setContracts(
        address pool,
        address cover,
        address vault
    ) public onlyOwner {
        require(
            pool != address(0) && cover != address(0) && vault != address(0),
            "Address cant be empty"
        );
        require(
            poolAddress == address(0) &&
                coverAddress == address(0) &&
                vaultContract == address(0),
            "Pool address already set"
        );

        coverAddress = cover;
        poolAddress = pool;
        vaultContract = vault;
    }

    function setNetworkMultiplier(
        uint256 chainId,
        uint256 multiplier
    ) external onlyOwner {
        require(
            multiplier > 0 && multiplier < 1100,
            "Multiplier must be greater than zero"
        );
        networkMultipliers[chainId] = multiplier;
    }

    modifier onlyBQContracts() {
        require(
            msg.sender == coverAddress ||
                msg.sender == initialOwner ||
                msg.sender == vaultContract ||
                msg.sender == poolAddress,
            "Caller is not the governance contract"
        );
        _;
    }
}

contracts/CoverLib.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.28;

library CoverLib {
    struct DepositParams {
        address depositor;
        uint256 poolId;
        uint256 amount;
        uint256 period;
        CoverLib.DepositType pdt;
        CoverLib.AssetDepositType adt;
        address asset;
    }

    struct Cover {
        uint256 id;
        string coverName;
        RiskType riskType;
        string chains;
        uint256 capacity;
        uint256 cost;
        uint256 capacityAmount;
        uint256 coverValues;
        uint256 maxAmount;
        uint256 poolId;
        string CID;
    }

    struct GenericCoverInfo {
        address user;
        uint256 coverId;
        RiskType riskType;
        string coverName;
        uint256 coverValue; // This is the value of the cover purchased
        uint256 claimPaid;
        uint256 coverPeriod; // This is the period the cover is purchased for in days
        uint256 endDay; // When the cover expires
        bool isActive;
    }

    enum RiskType {
        Low,
        Medium,
        High
    }

    struct GenericCover {
        RiskType riskType;
        bytes coverData;
    }

    enum AssetDepositType {
        Native,
        ERC20
    }

    enum DepositType {
        Normal,
        Vault
    }

    struct Pool {
        uint256 id;
        string poolName;
        CoverLib.RiskType riskType;
        uint256 apy;
        uint256 minPeriod;
        uint256 tvl;
        uint256 baseValue;
        uint256 coverTvl;
        uint256 tcp;
        bool isActive;
        uint256 percentageSplitBalance;
        uint256 investmentArmPercent;
        uint8 leverage;
        address asset;
        CoverLib.AssetDepositType assetType;
    }

    struct PoolParams {
        uint256 poolId;
        CoverLib.RiskType riskType;
        string poolName;
        uint256 apy;
        uint256 minPeriod;
        uint8 leverage;
        uint256 investmentArm;
        CoverLib.AssetDepositType adt;
        address asset;
    }

    struct Deposits {
        address lp;
        uint256 amount;
        uint256 poolId;
        uint256 dailyPayout;
        CoverLib.Status status;
        uint256 daysLeft;
        uint256 startDate;
        uint256 expiryDate;
        uint256 accruedPayout;
        CoverLib.DepositType pdt; // Vault deposit or normal pool deposit?
    }

    struct PoolInfo {
        string poolName;
        uint256 poolId;
        uint256 dailyPayout;
        uint256 depositAmount;
        uint256 apy;
        uint256 minPeriod;
        uint256 tvl;
        uint256 tcp; // Total claim paid to users
        bool isActive; // Pool status to handle soft deletion
        uint256 accruedPayout;
    }

    enum Status {
        Active,
        Due,
        Withdrawn
    }
}

contracts/InsuranceCover.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.28;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import "./CoverLib.sol";

interface IbqBTC {
    function bqMint(address account, uint256 amount) external;
    function burn(address account, uint256 amount) external;
    function transferFrom(
        address from,
        address to,
        uint256 amount
    ) external returns (bool);
}

interface ILP {
    struct Deposits {
        address lp;
        uint256 amount;
        uint256 poolId;
        uint256 dailyPayout;
        Status status;
        uint256 daysLeft;
        uint256 startDate;
        uint256 expiryDate;
        uint256 accruedPayout;
    }

    struct Pool {
        uint256 id;
        string poolName;
        CoverLib.RiskType riskType;
        uint256 apy;
        uint256 minPeriod;
        uint256 tvl;
        uint256 baseValue;
        uint256 coverTvl;
        uint256 tcp;
        bool isActive;
        uint256 percentageSplitBalance;
        uint256 investmentArmPercent;
        uint8 leverage;
        address asset;
        AssetDepositType assetType;
    }

    enum Status {
        Active,
        Expired
    }

    enum AssetDepositType {
        Native,
        ERC20
    }

    function getUserDeposit(
        uint256 _poolId,
        address _user
    ) external view returns (Deposits memory);

    function getVaultDeposits(
        uint256 vaultId,
        address user
    ) external view returns (Deposits[] memory);

    function getPool(
        uint256 _poolId
    )
        external
        view
        returns (
            string memory name,
            CoverLib.RiskType riskType,
            uint256 apy,
            uint256 minPeriod,
            uint256 tvl,
            bool isActive,
            uint256 percentageSplitBalance
        );

    function reducePercentageSplit(
        uint256 _poolId,
        uint256 __poolPercentageSplit
    ) external;
    function increasePercentageSplit(
        uint256 _poolId,
        uint256 __poolPercentageSplit
    ) external;
    function addPoolCover(
        uint256 _poolId,
        CoverLib.Cover memory _cover
    ) external;
    function updatePoolCovers(
        uint256 _poolId,
        CoverLib.Cover memory _cover
    ) external;
    function getPoolCovers(
        uint256 _poolId
    ) external view returns (CoverLib.Cover[] memory);
}

contract InsuranceCover is ReentrancyGuard, Ownable {
    using CoverLib for *;
    using Math for uint256;

    error LpNotActive();
    error InsufficientPoolBalance();
    error NoClaimableReward();
    error InvalidCoverDuration();
    error CoverNotAvailable();
    error UserHaveAlreadyPurchasedCover();
    error NameAlreadyExists();
    error InvalidAmount();
    error UnsupportedCoverType();
    error WrongPool();

    uint public coverFeeBalance;
    ILP public lpContract;
    IbqBTC public bqBTC;
    address public bqBTCAddress;
    address public lpAddress;
    address public governance;
    address[] public participants;
    mapping(address => uint256) public participation;

    mapping(uint256 => bool) public coverExists;
    mapping(address => mapping(uint256 => uint256)) public NextLpClaimTime;
    mapping(address => mapping(uint256 => uint256)) public LastVaultClaimTime;

    mapping(address => mapping(uint256 => CoverLib.GenericCoverInfo))
        public userCovers;
    mapping(uint256 => CoverLib.Cover) public covers;

    uint256 public coverCount;
    uint256[] public coverIds;

    event CoverCreated(
        uint256 indexed coverId,
        string name,
        CoverLib.RiskType riskType
    );
    event CoverPurchased(
        address indexed user,
        uint256 coverValue,
        uint256 coverFee,
        CoverLib.RiskType riskType
    );
    event PayoutClaimed(
        address indexed user,
        uint256 indexed poolId,
        uint256 amount
    );
    event CoverUpdated(
        uint256 indexed coverId,
        string coverName,
        CoverLib.RiskType riskType
    );

    constructor(
        address _lpContract,
        address _initialOwner,
        address _bqBTC
    ) Ownable(_initialOwner) {
        lpContract = ILP(_lpContract);
        lpAddress = _lpContract;
        bqBTC = IbqBTC(_bqBTC);
        bqBTCAddress = _bqBTC;
    }

    function createCover(
        uint256 coverId,
        string memory _cid,
        CoverLib.RiskType _riskType,
        string memory _coverName,
        string memory _chains,
        uint256 _capacity,
        uint256 _cost,
        uint256 _poolId
    ) public onlyOwner {
        uint256 _maxAmount = _validateAndGetPoolInfo(
            _coverName,
            _poolId,
            _riskType,
            _capacity
        );

        lpContract.reducePercentageSplit(_poolId, _capacity);

        coverCount++;
        CoverLib.Cover memory cover = CoverLib.Cover({
            id: coverId,
            coverName: _coverName,
            riskType: _riskType,
            chains: _chains,
            capacity: _capacity,
            cost: _cost,
            capacityAmount: _maxAmount,
            coverValues: 0,
            maxAmount: _maxAmount,
            poolId: _poolId,
            CID: _cid
        });
        covers[coverId] = cover;
        coverIds.push(coverId);
        lpContract.addPoolCover(_poolId, cover);
        coverExists[coverId] = true;

        emit CoverCreated(coverId, _coverName, _riskType);
    }

    function _validateAndGetPoolInfo(
        string memory _coverName,
        uint256 poolId,
        CoverLib.RiskType riskType,
        uint256 capacity
    ) internal view returns (uint256) {
        CoverLib.Cover[] memory coversInPool = lpContract.getPoolCovers(poolId);
        for (uint256 i = 0; i < coversInPool.length; i++) {
            if (
                keccak256(abi.encodePacked(coversInPool[i].coverName)) ==
                keccak256(abi.encodePacked(_coverName))
            ) {
                revert NameAlreadyExists();
            }
        }
        (
            ,
            CoverLib.RiskType risk,
            ,
            ,
            uint256 tvl,
            ,
            uint256 percentageSplitBalance
        ) = lpContract.getPool(poolId);

        if (risk != riskType || capacity > percentageSplitBalance) {
            revert WrongPool();
        }

        uint256 maxAmount = (tvl * ((capacity * 1e18) / 100)) / 1e18;
        return (maxAmount);
    }

    function updateCover(
        uint256 _coverId,
        string memory _coverName,
        CoverLib.RiskType _riskType,
        string memory _cid,
        string memory _chains,
        uint256 _capacity,
        uint256 _cost,
        uint256 _poolId
    ) public onlyOwner {
        (
            ,
            CoverLib.RiskType risk,
            ,
            ,
            uint256 tvl,
            ,
            uint256 _percentageSplitBalance
        ) = lpContract.getPool(_poolId);

        if (risk != _riskType || _capacity > _percentageSplitBalance) {
            revert WrongPool();
        }

        CoverLib.Cover storage cover = covers[_coverId];

        uint256 _maxAmount = (tvl * ((_capacity * 1e18) / 100)) / 1e18;

        if (cover.coverValues > _maxAmount) {
            revert WrongPool();
        }

        CoverLib.Cover[] memory coversInPool = lpContract.getPoolCovers(
            _poolId
        );
        for (uint256 i = 0; i < coversInPool.length; i++) {
            if (
                keccak256(abi.encodePacked(coversInPool[i].coverName)) ==
                keccak256(abi.encodePacked(_coverName)) &&
                coversInPool[i].id != _coverId
            ) {
                revert NameAlreadyExists();
            }
        }

        uint256 oldCoverCapacity = cover.capacity;

        cover.coverName = _coverName;
        cover.chains = _chains;
        cover.capacity = _capacity;
        cover.cost = _cost;
        cover.CID = _cid;
        cover.capacityAmount = _maxAmount;
        cover.poolId = _poolId;
        cover.maxAmount = _maxAmount - cover.coverValues;

        if (oldCoverCapacity > _capacity) {
            uint256 difference = oldCoverCapacity - _capacity;
            lpContract.increasePercentageSplit(_poolId, difference);
        } else if (oldCoverCapacity < _capacity) {
            uint256 difference = _capacity - oldCoverCapacity;
            lpContract.reducePercentageSplit(_poolId, difference);
        }

        lpContract.updatePoolCovers(_poolId, cover);

        emit CoverUpdated(_coverId, _coverName, _riskType);
    }

    function purchaseCover(
        uint256 _coverId,
        uint256 _coverValue,
        uint256 _coverPeriod,
        uint256 _coverFee
    ) public nonReentrant {
        if (_coverFee <= 0) {
            revert InvalidAmount();
        }
        if (_coverPeriod <= 27 || _coverPeriod >= 366) {
            revert InvalidCoverDuration();
        }
        if (!coverExists[_coverId]) {
            revert CoverNotAvailable();
        }

        CoverLib.Cover storage cover = covers[_coverId];
        if (_coverValue > cover.maxAmount) {
            revert InsufficientPoolBalance();
        }

        uint256 newCoverValues = cover.coverValues + _coverValue;

        if (newCoverValues > cover.capacityAmount) {
            revert InsufficientPoolBalance();
        }

        bqBTC.burn(msg.sender, _coverFee);

        cover.coverValues = newCoverValues;
        cover.maxAmount = cover.capacityAmount - newCoverValues;

        cover.maxAmount = (cover.capacityAmount - cover.coverValues);
        CoverLib.GenericCoverInfo storage userCover = userCovers[msg.sender][
            _coverId
        ];

        if (userCover.coverValue == 0) {
            userCovers[msg.sender][_coverId] = CoverLib.GenericCoverInfo({
                user: msg.sender,
                coverId: _coverId,
                riskType: cover.riskType,
                coverName: cover.coverName,
                coverValue: _coverValue,
                claimPaid: 0,
                coverPeriod: _coverPeriod,
                endDay: block.timestamp + (_coverPeriod * 1 days),
                isActive: true
            });
        } else {
            revert UserHaveAlreadyPurchasedCover();
        }

        bool userExists = false;
        for (uint i = 0; i < participants.length; i++) {
            if (participants[i] == msg.sender) {
                userExists = true;
                break;
            }
        }

        if (!userExists) {
            participants.push(msg.sender);
        }
        participation[msg.sender] += 1;

        coverFeeBalance += _coverFee;

        emit CoverPurchased(msg.sender, _coverValue, _coverFee, cover.riskType);
    }

    function getAllUserCovers(
        address user
    ) external view returns (CoverLib.GenericCoverInfo[] memory) {
        uint256 actualCount = 0;
        for (uint256 i = 0; i < coverIds.length; i++) {
            uint256 id = coverIds[i];
            if (userCovers[user][id].coverValue > 0) {
                actualCount++;
            }
        }

        CoverLib.GenericCoverInfo[]
            memory userCoverList = new CoverLib.GenericCoverInfo[](actualCount);

        uint256 index = 0;
        for (uint256 i = 0; i < coverIds.length; i++) {
            uint256 id = coverIds[i];
            if (userCovers[user][id].coverValue > 0) {
                userCoverList[index] = userCovers[user][id];
                index++;
            }
        }

        return userCoverList;
    }

    function getAllAvailableCovers()
        external
        view
        returns (CoverLib.Cover[] memory)
    {
        uint256 actualCount = 0;
        for (uint256 i = 0; i < coverIds.length; i++) {
            uint256 id = coverIds[i];
            if (coverExists[id]) {
                actualCount++;
            }
        }

        CoverLib.Cover[] memory availableCovers = new CoverLib.Cover[](
            actualCount
        );

        uint256 index = 0;
        for (uint256 i = 0; i < coverIds.length; i++) {
            uint256 id = coverIds[i];
            if (coverExists[id]) {
                availableCovers[index] = covers[id];
                index++;
            }
        }

        return availableCovers;
    }

    function getCoverInfo(
        uint256 _coverId
    ) external view returns (CoverLib.Cover memory) {
        return covers[_coverId];
    }

    function getUserCoverInfo(
        address user,
        uint256 _coverId
    ) external view returns (CoverLib.GenericCoverInfo memory) {
        return userCovers[user][_coverId];
    }

    function updateUserCoverValue(
        address user,
        uint256 _coverId,
        uint256 _claimPaid
    ) public onlyGovernance nonReentrant {
        userCovers[user][_coverId].coverValue -= _claimPaid;
        userCovers[user][_coverId].claimPaid += _claimPaid;
    }

    function deleteExpiredUserCovers(address _user) external nonReentrant {
        for (uint256 i = 1; i < coverIds.length; i++) {
            uint256 id = coverIds[i];
            CoverLib.GenericCoverInfo storage userCover = userCovers[_user][id];
            if (userCover.isActive && block.timestamp > userCover.endDay) {
                userCover.isActive = false;
                delete userCovers[_user][id];
            }
        }
    }

    function getCoverFeeBalance() external view returns (uint256) {
        return coverFeeBalance;
    }

    function updateMaxAmount(uint256 _coverId) public onlyPool nonReentrant {
        CoverLib.Cover storage cover = covers[_coverId];
        (, , , , uint256 tvl, , ) = lpContract.getPool(cover.poolId);
        // require(tvl > 0, "TVL is zero");
        require(cover.capacity > 0, "Invalid cover capacity");
        uint256 amount = (tvl * ((cover.capacity * 1e18) / 100)) / 1e18;
        covers[_coverId].capacityAmount = amount;
        covers[_coverId].maxAmount = (covers[_coverId].capacityAmount -
            covers[_coverId].coverValues);
    }

    function claimPayoutForLP(uint256 _poolId) external nonReentrant {
        ILP.Deposits memory depositInfo = lpContract.getUserDeposit(
            _poolId,
            msg.sender
        );
        if (depositInfo.status != ILP.Status.Active) {
            revert LpNotActive();
        }

        uint256 lastClaimTime;
        if (NextLpClaimTime[msg.sender][_poolId] == 0) {
            lastClaimTime = depositInfo.startDate;
        } else {
            lastClaimTime = NextLpClaimTime[msg.sender][_poolId];
        }

        uint256 currentTime = block.timestamp;
        if (currentTime > depositInfo.expiryDate) {
            currentTime = depositInfo.expiryDate;
        }

        // uint256 claimableDays = (currentTime - lastClaimTime) / 1 days;   Uncomment

        uint256 claimableDays = (currentTime - lastClaimTime) / 5 minutes;

        if (claimableDays <= 0) {
            revert NoClaimableReward();
        }
        uint256 claimableAmount = depositInfo.dailyPayout * claimableDays;

        if (claimableAmount > coverFeeBalance) {
            revert InsufficientPoolBalance();
        }
        NextLpClaimTime[msg.sender][_poolId] = block.timestamp;

        bqBTC.bqMint(msg.sender, claimableAmount);

        coverFeeBalance -= claimableAmount;

        emit PayoutClaimed(msg.sender, _poolId, claimableAmount);
    }

    function clamPayoutForVault(uint256 vaultId) external nonReentrant {
        ILP.Deposits[] memory deposits = lpContract.getVaultDeposits(
            vaultId,
            msg.sender
        );
        uint256 totalClaim;
        uint256 lastClaimTime;
        if (LastVaultClaimTime[msg.sender][vaultId] == 0) {
            lastClaimTime = deposits[0].startDate;
        } else {
            lastClaimTime = LastVaultClaimTime[msg.sender][vaultId];
        }

        uint256 currentTime = block.timestamp;
        if (currentTime > deposits[0].expiryDate) {
            currentTime = deposits[0].expiryDate;
        }
        uint256 claimableDays = (currentTime - lastClaimTime) / 5 minutes;

        for (uint256 i = 0; i < deposits.length; i++) {
            ILP.Deposits memory deposit = deposits[i];
            uint256 claimableAmount = deposit.dailyPayout * claimableDays;
            totalClaim += claimableAmount;
        }

        LastVaultClaimTime[msg.sender][vaultId] = block.timestamp;
        bqBTC.bqMint(msg.sender, totalClaim);

        coverFeeBalance -= totalClaim;

        emit PayoutClaimed(msg.sender, vaultId, totalClaim);
    }

    function getDepositClaimableDays(
        address user,
        uint256 _poolId
    ) public view returns (uint256) {
        ILP.Deposits memory depositInfo = lpContract.getUserDeposit(
            _poolId,
            user
        );

        uint256 lastClaimTime;
        if (NextLpClaimTime[user][_poolId] == 0) {
            lastClaimTime = depositInfo.startDate;
        } else {
            lastClaimTime = NextLpClaimTime[user][_poolId];
        }
        uint256 currentTime = block.timestamp;
        if (currentTime > depositInfo.expiryDate) {
            currentTime = depositInfo.expiryDate;
        }
        uint256 claimableDays = (currentTime - lastClaimTime) / 5 minutes;

        return claimableDays;
    }

    function getLastClaimTime(
        address user,
        uint256 _poolId
    ) public view returns (uint256) {
        return NextLpClaimTime[user][_poolId];
    }

    function getAllParticipants() public view returns (address[] memory) {
        return participants;
    }

    function getUserParticipation(address user) public view returns (uint256) {
        return participation[user];
    }

    modifier onlyGovernance() {
        require(msg.sender == governance, "Not authorized");
        _;
    }

    modifier onlyPool() {
        require(msg.sender == lpAddress, "Not authorized");
        _;
    }
}

contracts/InsurancePool.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.28;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./CoverLib.sol";

interface ICover {
    function updateMaxAmount(uint256 _coverId) external;
    function getDepositClaimableDays(
        address user,
        uint256 _poolId
    ) external view returns (uint256);
    function getLastClaimTime(
        address user,
        uint256 _poolId
    ) external view returns (uint256);
}

interface IVault {
    struct Pool {
        uint256 id;
        string poolName;
        CoverLib.RiskType riskType;
        uint256 apy;
        uint256 minPeriod;
        uint256 tvl;
        uint256 baseValue;
        uint256 coverTvl;
        uint256 tcp;
        bool isActive;
        uint256 percentageSplitBalance;
        uint256 investmentArmPercent;
        uint8 leverage;
        address asset;
        CoverLib.AssetDepositType assetType;
    }

    struct Vault {
        uint256 id;
        string vaultName;
        Pool[] pools;
        uint256 minInv;
        uint256 maxInv;
        uint256 minPeriod;
        CoverLib.AssetDepositType assetType;
        address asset;
    }

    struct VaultDeposit {
        address lp;
        uint256 amount;
        uint256 vaultId;
        uint256 dailyPayout;
        CoverLib.Status status;
        uint256 daysLeft;
        uint256 startDate;
        uint256 expiryDate;
        uint256 accruedPayout;
    }

    function getVault(uint256 vaultId) external view returns (Vault memory);
    function getUserVaultDeposit(
        uint256 vaultId,
        address user
    ) external view returns (VaultDeposit memory);
}

interface IbqBTC {
    function bqMint(address account, uint256 amount) external;
    function burn(address account, uint256 amount) external;
    function transferFrom(
        address from,
        address to,
        uint256 amount
    ) external returns (bool);
}

interface IGov {
    struct ProposalParams {
        address user;
        CoverLib.RiskType riskType;
        uint256 coverId;
        string txHash;
        string description;
        uint256 poolId;
        uint256 claimAmount;
    }

    struct Proposal {
        uint256 id;
        uint256 votesFor;
        uint256 votesAgainst;
        uint256 createdAt;
        uint256 deadline;
        uint256 timeleft;
        ProposalStaus status;
        bool executed;
        ProposalParams proposalParam;
    }

    enum ProposalStaus {
        Submitted,
        Pending,
        Approved,
        Claimed,
        Rejected
    }

    function getProposalDetails(
        uint256 _proposalId
    ) external returns (Proposal memory);
    function updateProposalStatusToClaimed(uint256 proposalId) external;
    function setUserVaultDepositToZero(uint256 vaultId, address user) external;
}

contract InsurancePool is ReentrancyGuard, Ownable {
    using CoverLib for *;

    mapping(address => mapping(uint256 => mapping(CoverLib.DepositType => CoverLib.Deposits))) deposits;
    mapping(uint256 => CoverLib.Cover[]) poolToCovers;
    mapping(uint256 => CoverLib.Pool) public pools;
    uint256 public poolCount;
    address public governance;
    ICover public ICoverContract;
    IVault public IVaultContract;
    IGov public IGovernanceContract;
    IbqBTC public bqBTC;
    address public bqBTCAddress;
    address public coverContract;
    address public vaultContract;
    address public initialOwner;
    address[] public participants;
    mapping(address => uint256) public participation;

    event Deposited(address indexed user, uint256 amount, string pool);
    event Withdraw(address indexed user, uint256 amount, string pool);
    event ClaimPaid(address indexed recipient, string pool, uint256 amount);
    event PoolCreated(uint256 indexed id, string poolName);
    event PoolUpdated(uint256 indexed poolId, uint256 apy, uint256 _minPeriod);
    event ClaimAttempt(uint256, uint256, address);

    constructor(address _initialOwner) Ownable(_initialOwner) {
        initialOwner = _initialOwner;
    }

    function createPool(
        CoverLib.PoolParams memory params
    ) public onlyOwner {
        require(
            params.adt == CoverLib.AssetDepositType.Native || params.asset != address(0),
            "Wrong Asset for Deposit"
        );

        poolCount += 1;
        CoverLib.Pool storage newPool = pools[params.poolId];
        newPool.id = params.poolId;
        newPool.poolName = params.poolName;
        newPool.apy = params.apy;
        newPool.minPeriod = params.minPeriod;
        newPool.tvl = 0;
        newPool.coverTvl = 0;
        newPool.baseValue = 0;
        newPool.isActive = true;
        newPool.riskType = params.riskType;
        newPool.investmentArmPercent = params.investmentArm;
        newPool.leverage = params.leverage;
        newPool.percentageSplitBalance = 100 - params.investmentArm;
        newPool.assetType = params.adt;

        emit PoolCreated(params.poolId, params.poolName);
    }

    function updatePool(
        uint256 _poolId,
        uint256 _apy,
        uint256 _minPeriod
    ) public onlyOwner {
        require(pools[_poolId].isActive, "Pool does not exist or is inactive");
        require(_apy > 0, "Invalid APY");
        require(_minPeriod > 0, "Invalid minimum period");

        pools[_poolId].apy = _apy;
        pools[_poolId].minPeriod = _minPeriod;

        emit PoolUpdated(_poolId, _apy, _minPeriod);
    }

    function reducePercentageSplit(
        uint256 _poolId,
        uint256 __poolPercentageSplit
    ) public onlyCover {
        pools[_poolId].percentageSplitBalance -= __poolPercentageSplit;
    }

    function increasePercentageSplit(
        uint256 _poolId,
        uint256 __poolPercentageSplit
    ) public onlyCover {
        pools[_poolId].percentageSplitBalance += __poolPercentageSplit;
    }

    function deactivatePool(uint256 _poolId) public onlyOwner {
        require(pools[_poolId].isActive, "Pool is not active");
        pools[_poolId].isActive = false;
    }

    function getPool(
        uint256 _poolId
    ) public view returns (CoverLib.Pool memory) {
        return pools[_poolId];
    }

    function getAllPools() public view returns (CoverLib.Pool[] memory) {
        CoverLib.Pool[] memory result = new CoverLib.Pool[](poolCount);
        for (uint256 i = 1; i <= poolCount; i++) {
            CoverLib.Pool memory pool = pools[i];
            result[i - 1] = CoverLib.Pool({
                id: i,
                poolName: pool.poolName,
                riskType: pool.riskType,
                apy: pool.apy,
                minPeriod: pool.minPeriod,
                tvl: pool.tvl,
                baseValue: pool.baseValue,
                coverTvl: pool.coverTvl,
                tcp: pool.tcp,
                isActive: pool.isActive,
                percentageSplitBalance: pool.percentageSplitBalance,
                investmentArmPercent: pool.investmentArmPercent,
                leverage: pool.leverage,
                asset: pool.asset,
                assetType: pool.assetType
            });
        }
        return result;
    }

    function updatePoolCovers(
        uint256 _poolId,
        CoverLib.Cover memory _cover
    ) public onlyCover {
        for (uint i = 0; i < poolToCovers[_poolId].length; i++) {
            if (poolToCovers[_poolId][i].id == _cover.id) {
                poolToCovers[_poolId][i] = _cover;
                break;
            }
        }
    }

    function addPoolCover(
        uint256 _poolId,
        CoverLib.Cover memory _cover
    ) public onlyCover {
        poolToCovers[_poolId].push(_cover);
    }

    function getPoolCovers(
        uint256 _poolId
    ) public view returns (CoverLib.Cover[] memory) {
        return poolToCovers[_poolId];
    }

    function getPoolsByAddress(
        address _userAddress
    ) public view returns (CoverLib.PoolInfo[] memory) {
        uint256 resultCount = 0;
        for (uint256 i = 1; i <= poolCount; i++) {
            if (
                deposits[_userAddress][i][CoverLib.DepositType.Normal].amount >
                0
            ) {
                resultCount++;
            }
        }

        CoverLib.PoolInfo[] memory result = new CoverLib.PoolInfo[](resultCount);

        uint256 resultIndex = 0;

        for (uint256 i = 1; i <= poolCount; i++) {
            CoverLib.Pool storage pool = pools[i];
            CoverLib.Deposits memory userDeposit = deposits[_userAddress][i][
                CoverLib.DepositType.Normal
            ];
            uint256 claimableDays = ICoverContract.getDepositClaimableDays(
                _userAddress,
                i
            );
            uint256 accruedPayout = userDeposit.dailyPayout * claimableDays;
            if (
                deposits[_userAddress][i][CoverLib.DepositType.Normal].amount >
                0
            ) {
                result[resultIndex++] = CoverLib.PoolInfo({
                    poolName: pool.poolName,
                    poolId: i,
                    dailyPayout: deposits[_userAddress][i][
                        CoverLib.DepositType.Normal
                    ].dailyPayout,
                    depositAmount: deposits[_userAddress][i][
                        CoverLib.DepositType.Normal
                    ].amount,
                    apy: pool.apy,
                    minPeriod: pool.minPeriod,
                    tvl: pool.tvl,
                    tcp: pool.tcp,
                    isActive: pool.isActive,
                    accruedPayout: accruedPayout
                });
            }
        }
        return result;
    }

    function poolWithdraw(uint256 _poolId) public nonReentrant {
        CoverLib.Pool storage selectedPool = pools[_poolId];
        CoverLib.Deposits storage userDeposit = deposits[msg.sender][_poolId][
            CoverLib.DepositType.Normal
        ];

        require(userDeposit.amount > 0, "No deposit found for this address");
        require(
            userDeposit.status == CoverLib.Status.Active,
            "Deposit is not active"
        );
        require(
            block.timestamp >= userDeposit.expiryDate,
            "Deposit period has not ended"
        );

        userDeposit.status = CoverLib.Status.Withdrawn;
        selectedPool.tvl -= userDeposit.amount;
        uint256 baseValue = selectedPool.tvl -
            ((selectedPool.investmentArmPercent * selectedPool.tvl) / 100);

        uint256 coverTvl = baseValue * selectedPool.leverage;
        selectedPool.coverTvl = coverTvl;
        selectedPool.baseValue = baseValue;
        CoverLib.Cover[] memory poolCovers = getPoolCovers(_poolId);
        for (uint i = 0; i < poolCovers.length; i++) {
            ICoverContract.updateMaxAmount(poolCovers[i].id);
        }

        if (selectedPool.assetType == CoverLib.AssetDepositType.ERC20) {
            bool success = IERC20(selectedPool.asset).transfer(
                msg.sender,
                userDeposit.amount
            );
            require(success, "ERC20 transfer failed");
        } else {
            (bool success, ) = msg.sender.call{value: userDeposit.amount}("");
            require(success, "Native asset transfer failed");
        }

        emit Withdraw(msg.sender, userDeposit.amount, selectedPool.poolName);
    }

    function withdrawUpdate(
        address depositor,
        uint256 _poolId,
        CoverLib.DepositType pdt
    ) public nonReentrant onlyVault {
        CoverLib.Pool storage selectedPool = pools[_poolId];
        CoverLib.Deposits storage userDeposit = deposits[depositor][_poolId][pdt];

        require(userDeposit.amount > 0, "No deposit found for this address");
        require(
            userDeposit.status == CoverLib.Status.Active,
            "Deposit is not active"
        );
        require(
            block.timestamp >= userDeposit.expiryDate,
            "Deposit period has not ended"
        );

        userDeposit.status = CoverLib.Status.Due;
        selectedPool.tvl -= userDeposit.amount;
        uint256 baseValue = selectedPool.tvl -
            ((selectedPool.investmentArmPercent * selectedPool.tvl) / 100);

        uint256 coverTvl = baseValue * selectedPool.leverage;
        selectedPool.coverTvl = coverTvl;
        selectedPool.baseValue = baseValue;
        CoverLib.Cover[] memory poolCovers = getPoolCovers(_poolId);
        for (uint i = 0; i < poolCovers.length; i++) {
            ICoverContract.updateMaxAmount(poolCovers[i].id);
        }

        emit Withdraw(depositor, userDeposit.amount, selectedPool.poolName);
    }

    function vaultWithdraw(uint256 _vaultId) public nonReentrant {
        IVault.VaultDeposit memory userVaultDeposit = IVaultContract
            .getUserVaultDeposit(_vaultId, msg.sender);
        require(userVaultDeposit.amount > 0, "No active withdrawal for user");
        IVault.Vault memory vault = IVaultContract.getVault(_vaultId);
        CoverLib.AssetDepositType adt = vault.assetType;
        if (adt == CoverLib.AssetDepositType.ERC20) {
            bool success = IERC20(vault.asset).transfer(
                msg.sender,
                userVaultDeposit.amount
            );
            require(success, "ERC20 transfer failed");
        } else {
            (bool success, ) = msg.sender.call{value: userVaultDeposit.amount}(
                ""
            );
            require(success, "Native asset transfer failed");
        }

        IVaultContract.getUserVaultDeposit(_vaultId, msg.sender);
    }

    function deposit(
        CoverLib.DepositParams memory depositParam
    ) public payable nonReentrant returns (uint256, uint256) {
        CoverLib.Pool storage selectedPool = pools[depositParam.poolId];

        require(selectedPool.isActive, "Pool is inactive or does not exist");
        require(
            selectedPool.assetType == depositParam.adt,
            "Pool does not accept this deposit type"
        );
        require(
            depositParam.period <= selectedPool.minPeriod,
            "Period too short"
        );
        require(
            selectedPool.asset == depositParam.asset,
            "Pool does not accept this asset"
        );
        require(
            deposits[depositParam.depositor][depositParam.poolId][
                depositParam.pdt
            ].amount == 0,
            "User has already deposited into this pool"
        );

        uint256 price;

        if (selectedPool.assetType == CoverLib.AssetDepositType.ERC20) {
            require(depositParam.amount > 0, "Amount must be greater than 0");
            IERC20(depositParam.asset).transferFrom(
                depositParam.depositor,
                address(this),
                depositParam.amount
            );
            selectedPool.tvl += depositParam.amount;
            price = depositParam.amount;
        } else {
            require(msg.value > 0, "Deposit cannot be zero");
            selectedPool.tvl += msg.value;
            price = msg.value;
        }

        uint256 baseValue = selectedPool.tvl -
            ((selectedPool.investmentArmPercent * selectedPool.tvl) / 100);

        uint256 coverTvl = baseValue * selectedPool.leverage;

        selectedPool.coverTvl = coverTvl;
        selectedPool.baseValue = baseValue;

        uint256 dailyPayout = (price * selectedPool.apy) / 100 / 365;
        CoverLib.Deposits memory userDeposit = CoverLib.Deposits({
            lp: depositParam.depositor,
            amount: price,
            poolId: depositParam.poolId,
            dailyPayout: dailyPayout,
            status: CoverLib.Status.Active,
            daysLeft: depositParam.period,
            startDate: block.timestamp,
            expiryDate: block.timestamp + (depositParam.period * 1 days),
            accruedPayout: 0,
            pdt: depositParam.pdt
        });

        if (depositParam.pdt == CoverLib.DepositType.Normal) {
            deposits[depositParam.depositor][depositParam.poolId][
                CoverLib.DepositType.Normal
            ] = userDeposit;
        } else {
            deposits[depositParam.depositor][depositParam.poolId][
                CoverLib.DepositType.Vault
            ] = userDeposit;
        }

        CoverLib.Cover[] memory poolCovers = getPoolCovers(depositParam.poolId);
        for (uint i = 0; i < poolCovers.length; i++) {
            ICoverContract.updateMaxAmount(poolCovers[i].id);
        }

        bool userExists = false;
        for (uint i = 0; i < participants.length; i++) {
            if (participants[i] == depositParam.depositor) {
                userExists = true;
                break;
            }
        }

        if (!userExists) {
            participants.push(depositParam.depositor);
        }

        participation[depositParam.depositor] += 1;

        emit Deposited(
            depositParam.depositor,
            depositParam.amount,
            selectedPool.poolName
        );

        return (price, dailyPayout);
    }

    function claimProposalFunds(uint256 _proposalId) public nonReentrant {
        IGov.Proposal memory proposal = IGovernanceContract.getProposalDetails(
            _proposalId
        );
        IGov.ProposalParams memory proposalParam = proposal.proposalParam;
        require(
            proposal.status == IGov.ProposalStaus.Approved && proposal.executed,
            "Proposal not approved"
        );
        CoverLib.Pool storage pool = pools[proposalParam.poolId];
        require(msg.sender == proposalParam.user, "Not a valid proposal");
        require(pool.isActive, "Pool is not active");
        require(
            pool.tvl >= proposalParam.claimAmount,
            "Not enough funds in the pool"
        );

        pool.tcp += proposalParam.claimAmount;
        pool.tvl -= proposalParam.claimAmount;
        CoverLib.Cover[] memory poolCovers = getPoolCovers(
            proposalParam.poolId
        );
        for (uint i = 0; i < poolCovers.length; i++) {
            ICoverContract.updateMaxAmount(poolCovers[i].id);
        }

        IGovernanceContract.updateProposalStatusToClaimed(_proposalId);

        emit ClaimAttempt(
            proposalParam.poolId,
            proposalParam.claimAmount,
            proposalParam.user
        );

        bqBTC.bqMint(msg.sender, proposalParam.claimAmount);

        emit ClaimPaid(msg.sender, pool.poolName, proposalParam.claimAmount);
    }

    function getUserDeposit(
        uint256 _poolId,
        address _user
    ) public view returns (CoverLib.Deposits memory) {
        CoverLib.Deposits memory userDeposit = deposits[_user][_poolId][
            CoverLib.DepositType.Normal
        ];
        uint256 claimTime = ICoverContract.getLastClaimTime(_user, _poolId);
        uint lastClaimTime;
        if (claimTime == 0) {
            lastClaimTime = userDeposit.startDate;
        } else {
            lastClaimTime = claimTime;
        }
        uint256 currentTime = block.timestamp;
        if (currentTime > userDeposit.expiryDate) {
            currentTime = userDeposit.expiryDate;
        }
        uint256 claimableDays = (currentTime - lastClaimTime) / 5 minutes;
        userDeposit.accruedPayout = userDeposit.dailyPayout * claimableDays;
        if (userDeposit.expiryDate <= block.timestamp) {
            userDeposit.daysLeft = 0;
        } else {
            uint256 timeLeft = userDeposit.expiryDate - block.timestamp;
            userDeposit.daysLeft = (timeLeft + 1 days - 1) / 1 days;
        }
        return userDeposit;
    }

    function getPoolTVL(uint256 _poolId) public view returns (uint256) {
        return pools[_poolId].tvl;
    }

    function poolActive(uint256 poolId) public view returns (bool) {
        CoverLib.Pool storage pool = pools[poolId];
        return pool.isActive;
    }

    function getAllParticipants() public view returns (address[] memory) {
        return participants;
    }

    function getUserParticipation(address user) public view returns (uint256) {
        return participation[user];
    }

    function setGovernance(address _governance) external onlyOwner {
        require(governance == address(0), "Governance already set");
        require(_governance != address(0), "Governance address cannot be zero");
        governance = _governance;
        IGovernanceContract = IGov(_governance);
    }

    function setCover(address _coverContract) external onlyOwner {
        require(coverContract == address(0), "Cover already set");
        require(_coverContract != address(0), "Cover address cannot be zero");
        ICoverContract = ICover(_coverContract);
        coverContract = _coverContract;
    }

    function setVault(address _vaultContract) external onlyOwner {
        require(vaultContract == address(0), "Vault already set");
        require(_vaultContract != address(0), "Vault address cannot be zero");
        IVaultContract = IVault(_vaultContract);
        coverContract = _vaultContract;
    }

    modifier onlyGovernance() {
        require(
            msg.sender == governance || msg.sender == initialOwner,
            "Caller is not the governance contract"
        );
        _;
    }

    modifier onlyCover() {
        require(
            msg.sender == coverContract || msg.sender == initialOwner,
            "Caller is not the cover contract"
        );
        _;
    }

    modifier onlyVault() {
        require(
            msg.sender == vaultContract || msg.sender == initialOwner,
            "Caller is not the vault contract"
        );
        _;
    }
}

contracts/Vaults.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.28;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./CoverLib.sol";

interface ICover {
    function updateMaxAmount(uint256 _coverId) external;
    function getDepositClaimableDays(
        address user,
        uint256 _poolId
    ) external view returns (uint256);
    function getLastClaimTime(
        address user,
        uint256 _poolId
    ) external view returns (uint256);
}

interface IbqBTC {
    function bqMint(address account, uint256 amount) external;
    function burn(address account, uint256 amount) external;
    function transferFrom(
        address from,
        address to,
        uint256 amount
    ) external returns (bool);
}

interface IPool {
    function deposit(
        CoverLib.DepositParams memory depositParam
    ) external payable returns (uint256, uint256);

    function withdrawUpdate(
        address depositor,
        uint256 _poolId,
        CoverLib.DepositType pdt
    ) external;
    function updateVaultWithdrawToDue(
        address user,
        uint256 vaultId,
        uint256 amount
    ) external;

    function getPool(
        uint256 _poolId
    ) external view returns (CoverLib.Pool memory);
}

interface IGov {
    struct ProposalParams {
        address user;
        CoverLib.RiskType riskType;
        uint256 coverId;
        string txHash;
        string description;
        uint256 poolId;
        uint256 claimAmount;
    }

    struct Proposal {
        uint256 id;
        uint256 votesFor;
        uint256 votesAgainst;
        uint256 createdAt;
        uint256 deadline;
        uint256 timeleft;
        ProposalStaus status;
        bool executed;
        ProposalParams proposalParam;
    }

    enum ProposalStaus {
        Submitted,
        Pending,
        Approved,
        Claimed,
        Rejected
    }

    function getProposalDetails(
        uint256 _proposalId
    ) external returns (Proposal memory);
    function updateProposalStatusToClaimed(uint256 proposalId) external;
}

contract Vaults is ReentrancyGuard, Ownable {
    using CoverLib for *;

    struct Vault {
        uint256 id;
        string vaultName;
        CoverLib.Pool[] pools;
        uint256 minInv;
        uint256 maxInv;
        uint256 minPeriod;
        CoverLib.AssetDepositType assetType;
        address asset;
    }

    struct Deposits {
        address lp;
        uint256 amount;
        uint256 poolId;
        uint256 dailyPayout;
        CoverLib.Status status;
        uint256 daysLeft;
        uint256 startDate;
        uint256 expiryDate;
        uint256 accruedPayout;
    }

    struct VaultDeposit {
        address lp;
        uint256 amount;
        uint256 vaultId;
        uint256 dailyPayout;
        CoverLib.Status status;
        uint256 daysLeft;
        uint256 startDate;
        uint256 expiryDate;
        uint256 accruedPayout;
    }

    struct PoolInfo {
        string poolName;
        uint256 poolId;
        uint256 dailyPayout;
        uint256 depositAmount;
        uint256 apy;
        uint256 minPeriod;
        uint256 tvl;
        uint256 tcp; // Total claim paid to users
        bool isActive; // Pool status to handle soft deletion
        uint256 accruedPayout;
    }

    mapping(uint256 => mapping(uint256 => uint256)) vaultPercentageSplits; //vault id to pool id to the pool percentage split;
    mapping(uint256 => Vault) vaults;
    mapping(address => mapping(uint256 => mapping(CoverLib.DepositType => Deposits))) deposits;
    mapping(address => mapping(uint256 => VaultDeposit)) userVaultDeposits;
    uint256 public vaultCount;
    address public governance;
    ICover public ICoverContract;
    IPool public IPoolContract;
    IGov public IGovernanceContract;
    IbqBTC public bqBTC;
    address public poolContract;
    address public bqBTCAddress;
    address public coverContract;
    address public initialOwner;
    address[] public participants;
    mapping(address => uint256) public participation;

    event Deposited(address indexed user, uint256 amount, string pool);
    event Withdraw(address indexed user, uint256 amount, string pool);
    event ClaimPaid(address indexed recipient, string pool, uint256 amount);
    event PoolCreated(uint256 indexed id, string poolName);
    event PoolUpdated(uint256 indexed poolId, uint256 apy, uint256 _minPeriod);
    event ClaimAttempt(uint256, uint256, address);

    constructor(address _initialOwner) Ownable(_initialOwner) {
        initialOwner = _initialOwner;
    }

    function createVault(
        string memory _vaultName,
        uint256[] memory _poolIds,
        uint256[] memory poolPercentageSplit,
        uint256 _minInv,
        uint256 _maxInv,
        uint256 _minPeriod,
        CoverLib.AssetDepositType adt,
        address asset
    ) public onlyOwner {
        require(
            _poolIds.length == poolPercentageSplit.length,
            "Mismatched pool IDs and percentages"
        );
        require(
            adt == CoverLib.AssetDepositType.Native || asset != address(0),
            "Invalid asset for deposit type"
        );

        vaultCount += 1;
        Vault storage vault = vaults[vaultCount];
        vault.id = vaultCount;
        vault.vaultName = _vaultName;
        vault.minInv = _minInv;
        vault.maxInv = _maxInv;
        vault.assetType = adt;
        vault.asset = asset;

        (uint256 percentageSplit, uint256 minPeriod) = validateAndSetPools(
            vault,
            _poolIds,
            poolPercentageSplit,
            adt
        );

        require(
            _minPeriod >= minPeriod,
            "Minimun period must be greater than or equal to the minimum period of all pools within the vault"
        );
        vault.minPeriod = _minPeriod;

        require(percentageSplit == 100, "Total split must equal 100%");
    }

    function initialVaultWithdraw(uint256 _vaultId) public nonReentrant {
        VaultDeposit storage userVaultDeposit = userVaultDeposits[msg.sender][
            _vaultId
        ];
        require(
            userVaultDeposit.amount > 0,
            "No deposit found for this address"
        );
        require(
            userVaultDeposit.status == CoverLib.Status.Active,
            "Deposit is not active"
        );
        require(
            block.timestamp >= userVaultDeposit.expiryDate,
            "Deposit period has not ended"
        );
        Vault memory vault = vaults[_vaultId];
        for (uint256 i = 0; i < vault.pools.length; i++) {
            uint256 poolId = vault.pools[i].id;
            IPoolContract.withdrawUpdate(
                msg.sender,
                poolId,
                CoverLib.DepositType.Vault
            );
        }

        userVaultDeposit.status = CoverLib.Status.Due;

        emit Withdraw(msg.sender, userVaultDeposit.amount, vault.vaultName);
    }

    function vaultDeposit(
        uint256 _vaultId,
        uint256 _amount,
        uint256 _period
    ) public payable nonReentrant {
        Vault memory vault = vaults[_vaultId];
        require(
            _period >= vault.minPeriod,
            "Period cannot be less than the vault min period"
        );
        uint256 totalDailyPayout = 0;
        uint256 totalAmount = 0;
        for (uint256 i = 0; i < vault.pools.length; i++) {
            uint256 poolId = vault.pools[i].id;
            CoverLib.DepositParams memory depositParam = CoverLib
                .DepositParams({
                    depositor: msg.sender,
                    poolId: poolId,
                    amount: _amount,
                    period: _period,
                    pdt: CoverLib.DepositType.Vault,
                    adt: vault.assetType,
                    asset: vault.asset
                });
            (uint256 amount, uint256 dailyPayout) = IPoolContract.deposit{
                value: msg.value
            }(depositParam);
            totalDailyPayout += dailyPayout;
            totalAmount += amount;
        }

        VaultDeposit memory userDeposit = VaultDeposit({
            lp: msg.sender,
            amount: totalAmount,
            vaultId: _vaultId,
            dailyPayout: totalDailyPayout,
            status: CoverLib.Status.Active,
            daysLeft: _period,
            startDate: block.timestamp,
            expiryDate: block.timestamp + (_period * 1 days),
            accruedPayout: 0
        });
        userVaultDeposits[msg.sender][_vaultId] = userDeposit;
        emit Deposited(msg.sender, _amount, vault.vaultName);
    }

    function validateAndSetPools(
        Vault storage vault,
        uint256[] memory _poolIds,
        uint256[] memory poolPercentageSplit,
        CoverLib.AssetDepositType adt
    ) internal returns (uint256 percentageSplit, uint256 minPeriod) {
        minPeriod = 365;
        for (uint256 i = 0; i < _poolIds.length; i++) {
            CoverLib.Pool memory pool = IPoolContract.getPool(_poolIds[i]);
            require(pool.assetType == adt, "Incompatible asset type in pool");
            percentageSplit += poolPercentageSplit[i];
            vaultPercentageSplits[vault.id][_poolIds[i]] = poolPercentageSplit[
                i
            ];
            vault.pools.push(pool);
            if (pool.minPeriod < minPeriod) {
                minPeriod = pool.minPeriod;
            }
        }
    }

    function getVault(uint256 vaultId) public view returns (Vault memory) {
        return vaults[vaultId];
    }

    function getVaultPools(
        uint256 vaultId
    ) public view returns (CoverLib.Pool[] memory) {
        return vaults[vaultId].pools;
    }

    function getUserVaultPoolDeposits(
        uint256 vaultId,
        address user
    ) public view returns (Deposits[] memory) {
        Vault memory vault = vaults[vaultId];
        Deposits[] memory vaultDeposits = new Deposits[](vault.pools.length);
        for (uint256 i = 0; i < vault.pools.length; i++) {
            uint256 poolId = vault.pools[i].id;
            vaultDeposits[i] = deposits[user][poolId][
                CoverLib.DepositType.Vault
            ];
        }

        return vaultDeposits;
    }

    function getUserVaultDeposit(
        uint256 vaultId,
        address user
    ) public view returns (VaultDeposit memory) {
        return userVaultDeposits[user][vaultId];
    }

    function setUserVaultDepositToZero(
        uint256 vaultId,
        address user
    ) public nonReentrant onlyPool {
        userVaultDeposits[user][vaultId].amount = 0;
    }

    function getVaults() public view returns (Vault[] memory) {
        Vault[] memory allVaults = new Vault[](vaultCount);
        for (uint256 i = 1; i <= vaultCount; i++) {
            allVaults[i - 1] = vaults[i];
        }

        return allVaults;
    }

    function setGovernance(address _governance) external onlyOwner {
        require(governance == address(0), "Governance already set");
        require(_governance != address(0), "Governance address cannot be zero");
        governance = _governance;
        IGovernanceContract = IGov(_governance);
    }

    function setCover(address _coverContract) external onlyOwner {
        require(coverContract == address(0), "Governance already set");
        require(
            _coverContract != address(0),
            "Governance address cannot be zero"
        );
        ICoverContract = ICover(_coverContract);
        coverContract = _coverContract;
    }

    modifier onlyGovernance() {
        require(
            msg.sender == governance || msg.sender == initialOwner,
            "Caller is not the governance contract"
        );
        _;
    }

    modifier onlyCover() {
        require(
            msg.sender == coverContract || msg.sender == initialOwner,
            "Caller is not the governance contract"
        );
        _;
    }

    modifier onlyPool() {
        require(
            msg.sender == poolContract || msg.sender == initialOwner,
            "Caller is not the governance contract"
        );
        _;
    }
}

